function manageIf(e,n){var t=window.innerHeight;return e.getBoundingClientRect().top<t-n}function manageAnimation(e,n){manageIf(e,n)&&e.classList.add("show")}let imgAbout=document.querySelector(".img-about img"),desAbout=document.querySelector(".des-about"),cardsAbout=document.querySelectorAll(".card-about"),imgMobile=document.querySelector(".img-app");window.addEventListener("scroll",e=>{manageAnimation(imgAbout,350),manageAnimation(desAbout,350),cardsAbout.forEach(manageAnimation),manageAnimation(imgMobile,350)});