let productContainers=[...document.querySelectorAll(".product-container")],nextBtn=[...document.querySelectorAll(".next")],preBtn=[...document.querySelectorAll(".pre")];productContainers.forEach((t,e)=>{let n=t.getBoundingClientRect().width-400;nextBtn[e].addEventListener("click",e=>{t.scrollLeft+=n}),preBtn[e].addEventListener("click",e=>{t.scrollLeft-=n})});const signinBtn=document.querySelector(".signinBtn"),signupBtn=document.querySelector(".signupBtn"),form=document.querySelector(".form-bx"),body=document.querySelector(".login-page");signupBtn.addEventListener("click",e=>{form.classList.add("active"),body.classList.add("active")}),signinBtn.addEventListener("click",e=>{form.classList.remove("active"),body.classList.remove("active")});let sizeBtns=document.querySelectorAll(".size-radio"),checkedBtn=0;sizeBtns.forEach((e,t)=>{e.addEventListener("click",()=>{sizeBtns[checkedBtn].classList.remove("check"),e.classList.add("check"),checkedBtn=t})});