let productContainers=[...document.querySelectorAll(".product-container")],nextBtn=[...document.querySelectorAll(".next")],preBtn=[...document.querySelectorAll(".pre")],sizeBtns=(productContainers.forEach((t,e)=>{let c=t.getBoundingClientRect().width-400;nextBtn[e].addEventListener("click",e=>{t.scrollLeft+=c}),preBtn[e].addEventListener("click",e=>{t.scrollLeft-=c})}),document.querySelectorAll(".size-radio")),checkedBtn=0;sizeBtns.forEach((e,t)=>{e.addEventListener("click",()=>{sizeBtns[checkedBtn].classList.remove("check"),e.classList.add("check"),checkedBtn=t})});