let sizeBtns=document.querySelectorAll(".size-radio");sizeBtns.forEach(e=>{e.addEventListener("click",e=>{e.target.parentElement.querySelectorAll(".check").forEach(e=>{e.classList.remove("check")}),e.target.classList.add("check")})});const setData=e=>{let t=document.querySelector("title"),a=(t.innerHTML+=e.data[0].name,document.querySelector(".img-slider")),c=(a.style.backgroundImage=`url(img/${e.data[0].images})`,document.querySelector(".details h2")),r=document.querySelector(".details p"),n=(c.innerHTML+=e.data[0].name,r.innerHTML+=e.data[0].des,document.querySelector(".price")),i=document.querySelector(".actual-price"),l=document.querySelector(".actual-price-dis");n.innerHTML+=e.data[0].actualPrice+"$",i.innerHTML+=e.data[0].sellPrice+"$",l.innerHTML+=`(${e.data[0].discount}% OFF)`};async function get(){const e=await fetch("/get-product.json");var t=await e.json();setData(t)}get();